import axios from "axios";
import router from '@/router'; 
import SockJS from 'sockjs-client';
import { Client } from '@stomp/stompjs';
//import Stomp from 'stompjs';


const state = {
  token: '',
  status: '',
  stompClient: null,
  emailVerify: false,
  user: JSON.parse(localStorage.getItem("user")) || null,
  clientId:
    "38181892421-dcmv3r9nvnkbalt85nskg16qtlc8e7n5.apps.googleusercontent.com",
  redirectUri: "http://localhost:8020/redirect",
  responseType: "code",
  scope: "email profile", // adjust scopes as needed
  state: "YOUR_STATE", // a random string to prevent CSRF attacks
  storeData: JSON.parse(localStorage.getItem("storeData")) || null,
};

const mutations = {
  auth_request(state) {
    state.status = "loading";
  },
  auth_success(state, payload) {
    state.status = "";
    state.token = payload.token;
    state.user = payload.user;
    localStorage.setItem("token", payload.token);
    localStorage.setItem("user", JSON.stringify(payload.user));
  },

  setUser(state, requestUser) {
    state.user = requestUser;
    localStorage.setItem("user", JSON.stringify(requestUser));
    if (!requestUser.storeId) {
      localStorage.removeItem("storeData");
    }
  },

  setStore(state, requestStore) {
    state.storeData = requestStore;
    localStorage.setItem("storeData", JSON.stringify(requestStore));
  },
  setIsEmailVerifyStatus(state, verifyStatus) {
    state.emailVerify = verifyStatus;
  },

  auth_error(state) {
    state.status = "error";
  },
  logout(state) {
    state.status = "";
    state.token = "";
    state.user = null;
    localStorage.removeItem("token");
    localStorage.removeItem("user");
    localStorage.removeItem("storeData");
  },
  loginWithOAuth() {
    console.log("oauth");
    const authUrl = `https://accounts.google.com/o/oauth2/v2/auth?client_id=${state.clientId}&redirect_uri=${state.redirectUri}&response_type=${state.responseType}&scope=${state.scope}&state=${state.state}`;
    window.location.href = authUrl;
  },
  setStompClient(state, stompClient) {
    state.stompClient = stompClient;
  },
};

const actions = {
  connectWebSocket({ commit, dispatch }) {
    const socket = new SockJS('/ws');//create new socket connection to the websocket
    const stompClient = new Client({//new stomp client is created.
      webSocketFactory: () => socket,//webSocketFactory is used to provide the SockJs intance created earlier
      onConnect: () => {//this callback is executed when the websocket connection is successfully established
        stompClient.subscribe('/topic/verification', message => {//subscribe to a topic to receive real-time updates
          const isVerified = JSON.parse(message.body);
          console.log(isVerified);
          commit('setIsEmailVerifyStatus', isVerified);
        });
      },
      onStompError: (frame) => {
        console.error('Broker reported error: ' + frame.headers['message']);
        console.error('Additional details: ' + frame.body);//The onStompError callback handles any errors reported by the STOMP broker, logging them to the console.
      },
    });

    stompClient.activate();//activate the stomp client
    commit('setStompClient', stompClient);
  },
  async register({ commit, dispatch}, user) {
    try {
      dispatch("connectWebSocket")
      commit("auth_request");
      const response = await axios.post("/api/v1/auth/getRegisterEmail", user);
      console.log(response);
      return response;
    } catch (error) {
      throw error;
    }
  },

  // async register({ commit, dispatch}, user) {
  //   try {
  //     commit("auth_request");
  //     const response = await axios.post("/api/v1/auth/register", user);
  //     console.log(state.token)
  //     dispatch("getUser");
  //     //const token = response.data.token;
  //     return response;
  //   } catch (error) {
  //     throw error;
  //   }
  // },

  async login({ commit, dispatch }, user) {
    try {
      commit("auth_request");
      const response = await axios.post("/api/v1/auth/authenticate", user);
      const token = response.data.token;
      dispatch("getUser");
      // axios.defaults.headers.common["Authorization"] = `Bearer ${token}`;
      // const userDetails = await axios.get("/api/users/" + user.email, {
      //   headers: {
      //     Authorization: `Bearer ${token}`,
      //   },
      // });
      // const userDto = userDetails.data;
      //commit("auth_success", { token, user: userDto });
      return response;
    } catch (error) {
      commit("auth_error");
      localStorage.removeItem("token");
      delete axios.defaults.headers.common["Authorization"];
      throw error;
    }
  },

  async oauth({ commit, dispatch }, user) {
    try {
      commit("auth_request");
      const response = await axios.post("/api/v1/auth/oauth", user);
      const token = response.data.token;
      const userDetails = await axios.get("/api/users/" + user.email, {
        headers: {
          Authorization: `Bearer ${token}`,
        },
      });
      const userDto = userDetails.data;
      console.log("UserDto : ", userDto);
      commit("auth_success", { token, user: userDto });

      return response;
    } catch (error) {
      console.log(error);
      if (error.response.status === 403) {
        commit("auth_error");
        throw new Error("oauth denied");
      } else {
        commit("auth_error");
        throw error;
      }
    }
  },

  async getUser({ state, commit }) {

    const url = "/api/users/getsUserFromJwtToken"; // Replace with your API endpoint

    try {
      const response = await axios.get(url, {
        withCredentials: true, // Ensure cookies are sent with the request
      });
      const userDto = response.data;
      commit("setUser", userDto);

      if (userDto.storeId) {
        const getStoreUrl = "/api/stores/" + state.user.storeId;

        try {
          const response = await axios.get(getStoreUrl, {
            withCredentials: true, // Ensure cookies are sent with the request
          });
          commit("setStore", response.data);
        } catch (error) {
          console.error("There was an error: ", error);
          if (error.response && error.response.status === 404) {
            console.log("Store not found");
          }
        }
      } else {
        localStorage.removeItem("storeData");
      }
    } catch (error) {
      localStorage.removeItem("user");//todo remove all the local storage date
      console.error("There was an error!", error);
      throw error;
    }
  },


  async logout({ commit }) {
    commit("logout");
    try{
      const response = await axios.get("/api/logout",{
        withCredential: true,
      });
    }catch(error){
      console.error(error);
    }
    //delete axios.defaults.headers.common["Authorization"];
    router.push("/")
  },
};

const getters = {
  isAuthenticated: (state) => !!state.user,
  authStatus: (state) => state.status,
  showName: (state) => state.user.showName,
  user: (state) => state.user,
  storeId: (state) => state.user.storeId,
  storeData: (state) => state.storeData,
};

export default {
  namespaced: true,
  state,
  mutations,
  actions,
  getters,
};
